
function bw(){$('.slider').toggleClass('bw');$('.offer').toggleClass('bw');$('.quest').toggleClass('bw');$('.blog').toggleClass('bw');$('.opinions').toggleClass('bw');$('.page').toggleClass('bw');$('.foobar').toggleClass('bw');$('.navbar__info').toggleClass('bw');}
function addPar(){box=document.querySelectorAll('.page');if(box){p=document.querySelectorAll('p');for(i=0;i<p.length;i++){p[i].classList.add('paragraph__text');}}}
addPar();function form(){form=document.querySelectorAll('input');form.classList.remove('')}
function createBtnSlide(name){var box=document.getElementById(name);if(box){var a=box.querySelectorAll(".slider__slide");if(a.length>1){var container=box.querySelectorAll('.slider__controls');if(container.length>0){for(i=0;i<a.length;i++){btn=document.createElement('button');btn.classList.add('btn','btn__control');btn.setAttribute('name','btn-cntrol')
btn.setAttribute('role','button')
btn.setAttribute('value',i);container[0].appendChild(btn);}
btn=container[0].querySelectorAll('.btn');btn[0].classList.add('btn__control--active');}}}}
function autoSlider(z){var box=document.getElementById(z);if(box){var a=box.querySelectorAll(".slider__slide");var b=box.querySelectorAll(".btn__control");var m=box.querySelectorAll(".mask");end=a.length-1;if(b.length>0){if(a.length>0){for(i=0;i<a.length;i++){if(a[i].classList.contains('slider__slide--display')&&b[i].classList.contains('btn__control--active')&&m[i].classList.contains('mask--display')==true){if(i==end){a[0].classList.add('slider__slide--display','animated','fadeInRight')
b[0].classList.add('btn__control--active','animated','pulse');m[0].classList.add('mask--display');}
a[i].classList.remove('slider__slide--display','animated','fadeInRight')
b[i].classList.remove('btn__control--active','animated','pulse');m[i].classList.remove('mask--display');a[++i].classList.add('slider__slide--display','animated','fadeInRight')
b[i].classList.add('btn__control--active','animated','pulse');m[i].classList.add('mask--display');}}}}else{if(a.length>0){for(i=0;i<a.length;i++){if(a[i].classList.contains('slider__slide--display')==true){if(i==end){a[0].classList.add('slider__slide--display','animated','fadeInRight')}
a[i].classList.remove('slider__slide--display','animated','fadeInRight')
a[++i].classList.add('slider__slide--display','animated','fadeInRight')}}}}}}
function sliderClick(z,name){var box=document.getElementById(name);if(box){var a=box.querySelectorAll(".slider__slide");var m=box.querySelectorAll(".mask");for(i=0;i<a.length;i++){a[i].classList.remove('slider__slide--display','animated','fadeInRight')
m[i].classList.add('mask--display');}
a[z].classList.add('slider__slide--display','animated','fadeInRight')
m[z].classList.add('mask--display');var a=box.querySelectorAll(".btn__control");for(i=0;i<a.length;i++){a[i].classList.remove('btn__control--active')
m[i].classList.remove('mask--display')}
a[z].classList.add('btn__control--active')
m[z].classList.add('mask--display');}}
function addDisplay(nameSlider){var box=document.getElementById(nameSlider);if(box){var body=box.querySelectorAll(".slider__body");var slide=body[0].querySelectorAll(".slider__slide");var mask=body[0].querySelectorAll(".mask");if(slide.length>0){slide[0].classList.add('slider__slide--display');}
if(mask.length>0){mask[0].classList.add('mask--display');}}}
document.addEventListener("DOMContentLoaded",function(){addDisplay("sliderOne");addDisplay("sliderTwo");createBtnSlide("sliderOne");createBtnSlide("sliderTwo");setInterval(function(){autoSlider("sliderOne");},5000);setInterval(function(){autoSlider("sliderTwo");},2000);$("#sliderOne .btn__control").click(function(){const a=$(this).attr('value');sliderClick(a,"sliderOne");});$('.btn__place').click(function(){$('.items__place').toggleClass('items__place--active animated fadeInDown');})
$('#modal-message').click(function(){$('#message').addClass('modal--display');})
$('#modal-call').click(function(){$('#call').addClass('modal--display');})
$('.btn__modal-close').click(function(){$('#message').removeClass('modal--display');$('#call').removeClass('modal--display');})
$('.btn__toggle').click(function(){$('.navbar__collpase').toggleClass('navbar__collpase--display animated fadeIn');$('.navbar__menu').toggleClass('animated fadeInRight');$('.btn__close').toggleClass('animated fadeInRight');bw();})
$('.btn__close').click(function(){$('.navbar__menu').toggleClass('fadeOutRight');$('.btn__close').toggleClass('fadeOutRight');setTimeout(function(){$('.navbar__collpase').toggleClass('navbar__collpase--display animated fadeIn');$('.navbar__menu').toggleClass('animated fadeOutRight fadeInRight');$('.btn__close').toggleClass('animated fadeOutRight fadeInRight');},900)
bw();})});